package algorithms.dynamicprogramming.longestpalindromicsubstring

import org.junit.Assert.*
import org.junit.Test

class LongestPalindromicSubstringTest {

    private val bigInput = ""
    private val bigInput2 = ""
    private val bigInput3 = ""

    @Test
    fun longestPalindrome() {
        assertEquals("", longestPalindrome(""))
        assertEquals("a", longestPalindrome("a"))
        kotlin.test.assertTrue(longestPalindrome("ab") in setOf("a", "b"))
        kotlin.test.assertTrue(longestPalindrome("babad") in setOf("bab", "aba"))
        assertEquals("bb", longestPalindrome("cbbd"))
        assertEquals("abcba", longestPalindrome("abcba"))
        assertEquals("bcb", longestPalindrome("abcbz"))
        assertEquals("a", longestPalindrome("abcda"))
        assertEquals(bigInput, longestPalindrome(bigInput))
        assertEquals(bigInput2, longestPalindrome(bigInput2))
    }

}